    public class CompaniesAppServiceTests : MdmServiceApplicationTestBase
    {
        private readonly ICompaniesAppService _companiesAppService;
        private readonly IRepository<Company, Guid> _companyRepository;

        public CompaniesAppServiceTests()
        {
            _companiesAppService = GetRequiredService<ICompaniesAppService>();
            _companyRepository = GetRequiredService<IRepository<Company, Guid>>();
        }

        [Fact]
        public async Task GetListAsync()
        {
            // Act
            var result = await _companiesAppService.GetListAsync(new GetCompaniesInput());

            // Assert
            result.TotalCount.ShouldBe(2);
            result.Items.Count.ShouldBe(2);
            result.Items.Any(x => x.Company.Id == Guid.Parse("b0aca71d-adf2-47c1-a39a-b1e2ff33dcfc")).ShouldBe(true);
            result.Items.Any(x => x.Company.Id == Guid.Parse("68a46bca-710b-45cb-87e1-4d2b6852e8da")).ShouldBe(true);
        }

        [Fact]
        public async Task GetAsync()
        {
            // Act
            var result = await _companiesAppService.GetAsync(Guid.Parse("b0aca71d-adf2-47c1-a39a-b1e2ff33dcfc"));

            // Assert
            result.ShouldNotBeNull();
            result.Id.ShouldBe(Guid.Parse("b0aca71d-adf2-47c1-a39a-b1e2ff33dcfc"));
        }

